import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { IoIosSearch } from 'react-icons/io';
import { CiStar } from 'react-icons/ci';
import { getUser } from '../services/userAPI';
import Loading from '../pages/Loading';
import logo from '../images/logo.png';
import styles from './Header.module.css';

class Header extends Component {
  state = {
    userName: '',
    completedRequisition: false,
  };

  async componentDidMount() {
    const usrName = await this.fetchName();

    this.setState({
      userName: usrName,
      completedRequisition: true,
    });
  }

  fetchName = async () => {
    const name = await getUser();
    return name.name;
  };

  render() {
    const { userName, completedRequisition } = this.state;
    return (
      <header data-testid="header-component" className={ styles.header }>
        <img src={ logo } alt={ logo } className={ styles.headerLogo } />

        <nav>
          <Link to="/search" data-testid="link-to-search">
            <IoIosSearch />
            <p>avatar

              Pesquisa
            </p>
          </Link>
          <Link to="/favorites" data-testid="link-to-favorites">
            <CiStar />
            <p>
              Favoritos
            </p>
          </Link>
          <Link to="/profile" data-testid="link-to-profile">Perfil</Link>
        </nav>
        { completedRequisition ? <h1 data-testid="header-user-name">{ userName }</h1>
          : <Loading header /> }
      </header>
    );
  }
}

export default Header;
