import React, { Component } from 'react';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import CardAlbum from '../components/CardAlbum';
import styles from './Search.module.css';
import Loading from './Loading';

class Search extends Component {
  state = {
    isDisabledButtton: true,
    searchInput: '',
    albuns: [],
    artistName: '',
    isLoading: false,
  };

  handleChangle = ({ target }) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
    }, () => {
      if (value.length >= 2) {
        this.setState({
          isDisabledButtton: false,
        });
      } else {
        this.setState({
          isDisabledButtton: true,
        });
      }
    });
  };

  searching = async () => {
    const { searchInput } = this.state;
    this.setState({
      searchInput: '',
      albuns: [],
      artistName: searchInput,
      isLoading: true,
    });
    const fetchAlbuns = await searchAlbumsAPI(searchInput);
    this.setState({
      albuns: fetchAlbuns,
    });
  };

  render() {
    const { isDisabledButtton, searchInput, albuns, artistName, isLoading } = this.state;
    return (
      <div data-testid="page-search" className={ styles.container }>
        <Header />
        <main className={ styles.search }>
          <form className={ styles.searchForm }>
            <input
              type="text"
              data-testid="search-artist-input"
              name="searchInput"
              value={ searchInput }
              onChange={ this.handleChangle }
              placeholder="digite a sua pesquisa"
            />
            <button
              type="button"
              data-testid="search-artist-button"
              disabled={ isDisabledButtton }
              onClick={ this.searching }
            >
              Pesquisar
            </button>
          </form>
          <section>
            <h2>{ `Resultado de álbuns de: ${artistName}`}</h2>
          </section>

          {
            isLoading && <Loading  />
          }
          <ul className="albuns-list">
            {
              albuns.length === 0
                ? <p>Nenhum álbum foi encontrado</p>
                : albuns.map((album) => (
                  <CardAlbum
                    key={ album.collectionId }
                    albumImg={ album.artworkUrl100 }
                    albumName={ album.collectionName }
                    artistName={ album.artistName }
                    collectionId={ album.collectionId }
                  />
                ))
            }
          </ul>
        </main>
      </div>
    );
  }
}

export default Search;
