import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import { getUser } from '../services/userAPI';
import Loading from './Loading';
import styles from './Profile.module.css';

class Profile extends Component {
  state = {
    userName: '',
    userEmail: '',
    userImage: '',
    userDescription: '',
    requestIsDone: true,
  };

  componentDidMount() {
    this.fetchUser();
  }

  fetchUser = async () => {
    this.setState({
      requestIsDone: false,
    });
    const user = await getUser();
    this.setState({
      userName: user.name,
      userEmail: user.email,
      userImage: user.image,
      userDescription: user.description,
      requestIsDone: true,
    });
  };

  render() {
    const { userName, userEmail, userDescription, userImage, requestIsDone } = this.state;

    const profileInfos = (
      <section className="profile-Infos">
        <h3>Nome:</h3>
        <p>{ userName }</p>
        <h3>Email:</h3>
        <p>{ userEmail }</p>
        <h3>Descrição</h3>
        <p>{ userDescription }</p>
        <Link to="/profile/edit">Editar perfil</Link>
      </section>
    );

    return (
      <div data-testid="page-profile" className={ styles.container }>
        <Header />
        <section className={ styles.perfil }>
          <header>
            {
              userImage === ''
                ? <img src="https://www.gravatar.com/avatar/205e460b479e2e5b48aec07710c08d50" alt="Foto do usuário" data-testid="profile-image" />
                : <img
                    src={ userImage } alt="Foto do usuário" data-testid="profile-image"
                />
            }
            <img src={ userImage } alt="Foto do usuário" data-testid="profile-image" />
          </header>
          { !requestIsDone ? <Loading /> : profileInfos}
        </section>
      </div>
    );
  }
}

export default Profile;
